const Disp = ({arg}) => (
  <View style={styles.flatcontainer}>
    <View style={{marginLeft: 330}}>
      <Pressable onPress={() => Alert.alert(`Calling ${arg.name}`)}>
        <Image
          source={{
            uri: 'data:image/png;base64,',
          }}
          style={styles.image}
        />
      </Pressable>
    </View>
    <Image source={{uri: arg.Image}} style={styles.dummy} />
    <Text style={styles.txtname}>{arg.name}</Text>
    <Text style={styles.txt}>{arg.designation}</Text>
    <Text style={styles.txt}>{arg.phone}</Text>
  </View>
);
const App = () => {
  const num = ARR.sort((a, b) => (a.name > b.name ? 1 : -1));
  const [Data, setData] = useState([num]);
  const Details = ({item}) => <Disp arg={item} />;
  const fun = i => {
    if (i) {
      const fil = ARR.filter(arr => {
        const a = arr.name.toUpperCase();
        const b = i.toUpperCase();
        return a.startsWith(b);
      });
      setData(fil);
    } else {
      setData(ARR);
    }
  };
  return (
    <SafeAreaView style={styles.view}>
      <Text style={styles.welcome}>Contact List</Text>
      <View style={styles.searchView}>
        <Image
          source={{
            uri: 'https://www.kindpng.com/picc/m/25-254282_search-button-icon-png-transparent-png.png',
          }}
          style={styles.search}></Image>
        <TextInput
          style={styles.input}
          placeholder="search"
          onChangeText={arg1 => {
            fun(arg1);
          }}></TextInput>
      </View>

      <FlatList data={Data} renderItem={Details} />
    </SafeAreaView>
  );
};
